mapper.select:
SELECT

FROM act_yw_group a

WHERE a.id = #{id}

SELECT

FROM act_yw_group a

WHERE a.keyss = #{keyss}

SELECT

FROM act_yw_group a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.status = #{status}


		AND a.type LIKE
			'%'||#{type}||',%'
			'%'+#{type}+',%'
			concat('%',#{type},',%')


		AND a.flow_type = #{flowType}


		AND a.theme = #{theme}


		AND a.temp = #{temp}


		AND a.keyss = #{keyss}


		AND a.author = #{author}


		AND a.version = #{version}


		AND a.flow_id = #{flowId}


		AND a.sort = #{sort}




		ORDER BY ${page.orderBy} ${page.orderByType}


		ORDER BY a.theme DESC, a.type ASC, a.update_date DESC, a.keyss ASC, a.sort ASC

SELECT

FROM act_yw_group a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.status = #{status}


		AND a.type LIKE
			'%'||#{type}||',%'
			'%'+#{type}+',%'
			concat('%',#{type},',%')


		AND a.flow_type = #{flowType}


		AND a.theme = #{theme}


		AND a.temp = #{temp}


		AND a.keyss = #{keyss}


		AND a.author = #{author}


		AND a.version = #{version}


		AND a.flow_id = #{flowId}


		AND a.sort = #{sort}


GROUP BY a.id


		ORDER BY ${page.orderBy} ${page.orderByType}


		ORDER BY a.theme DESC, a.type ASC, a.update_date DESC, a.keyss ASC, a.sort ASC

SELECT count(a.id) FROM act_yw_group a

	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE
			'%'||#{name}||'%'
			'%'+#{name}+'%'
