		AND a.flow_type = #{flowType}


		AND a.theme = #{theme}


		AND a.temp = #{temp}


		AND a.keyss = #{keyss}


		AND a.author = #{author}


		AND a.version = #{version}


		AND a.flow_id = #{flowId}


		AND a.sort = #{sort}

SELECT

FROM act_yw_group a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


