mapper.select:
SELECT

FROM act_yw_gtheme a

WHERE a.id = #{id}

SELECT

FROM act_yw_gtheme a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		and a.enable=#{enable}




		ORDER BY ${page.orderBy} ${page.orderByType}


		ORDER BY a.sort, a.update_date DESC

SELECT

FROM act_yw_gtheme a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy} ${page.orderByType}


		ORDER BY a.sort, a.update_date DESC

mapper.update:
