mapper.select:
SELECT

FROM act_hi_taskinst a

WHERE a.id = #{id}

SELECT
FROM act_hi_taskinst a
1 = 1 AND pd.name != ""
AND a.proc_inst_id_ = #{procInstId}
AND a.proc_def_id_ = #{procDefId}
AND pd.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND pd.name LIKE '%'||#{projectDeclare.name}||'%' '%'+#{projectDeclare.name}+'%' concat('%',#{projectDeclare.name},'%')
AND pd.type = #{projectDeclare.type}
GROUP BY b.id_
ORDER BY ${page.orderBy}
ORDER BY b.id_ desc, pd.name desc

SELECT
FROM act_hi_taskinst a
1 = 1 AND pd.name != ""
AND a.proc_inst_id_ = #{procInstId}
AND a.proc_def_id_ = #{procDefId}
AND pd.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND pd.name LIKE '%'||#{projectDeclare.name}||'%' '%'+#{projectDeclare.name}+'%' concat('%',#{projectDeclare.name},'%')
AND pd.type = #{projectDeclare.type}
GROUP BY b.id_
ORDER BY ${page.orderBy}
ORDER BY b.id_ desc, pd.name desc
