mapper.select:
SELECT

FROM act_yw_node a

WHERE a.id = #{id}

		SELECT

		FROM act_yw_node a


                        a.del_flag = #{DEL_FLAG_NORMAL}

				AND a.name LIKE
					'%'||#{name}||'%'
					'%'+#{name}+'%'
					concat('%',#{name},'%')


				AND a.type = #{type}


				AND a.is_form = #{isForm}


				AND a.is_visible = #{isVisible}


				AND a.node_type = #{nodeType}


				AND a.node_key = #{nodeKey}




				ORDER BY ${page.orderBy}


				ORDER BY a.type ASC, a.update_date DESC

SELECT

FROM act_yw_node a


	a.del_flag = #{DEL_FLAG_NORMAL}
	AND a.type  <>  0

		AND a.name LIKE
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.type = #{type}


		AND a.is_form = #{isForm}


		AND a.is_visible = #{isVisible}


		AND a.node_type = #{nodeType}


		AND a.node_key = #{nodeKey}




		ORDER BY ${page.orderBy}


		ORDER BY a.type ASC, a.update_date DESC

SELECT

FROM act_yw_node a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.type ASC, a.update_date DESC

mapper.update:
UPDATE act_yw_node SET
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
