mapper.select:
SELECT

FROM act_ru_execution a

WHERE a.id = #{id}

SELECT

FROM act_ru_execution a


	1 = 1 AND pd.name != ""

		AND a.proc_inst_id_ = #{procInstId}


		AND a.proc_def_id_ = #{procDefId}


		AND a.business_key_ = #{businessKey}


		AND a.is_active_ = #{isActive}




			AND pd.name LIKE
				'%'||#{projectDeclare.name}||'%'
				'%'+#{projectDeclare.name}+'%'
				concat('%',#{projectDeclare.name},'%')


			AND pd.type = #{projectDeclare.type}


	GROUP BY b.id_



		ORDER BY ${page.orderBy}


		ORDER BY b.id_ desc, pd.name desc, p.parent_id_ desc

SELECT

FROM act_ru_execution a


	1 = 1 AND pd.name != ""

		AND a.proc_inst_id_ = #{procInstId}


		AND a.proc_def_id_ = #{procDefId}


		AND a.business_key_ = #{businessKey}


		AND a.is_active_ = #{isActive}




			AND pd.name LIKE
				'%'||#{projectDeclare.name}||'%'
				'%'+#{projectDeclare.name}+'%'
				concat('%',#{projectDeclare.name},'%')


			AND pd.type = #{projectDeclare.type}


	GROUP BY b.id_



		ORDER BY ${page.orderBy}


		ORDER BY b.id_ desc, pd.name desc, p.parent_id_ desc
