mapper.select:
SELECT COUNT(*) FROM (
SELECT g.p_name ,g.id ,g.declare_id ,t.user_id
FROM g_contest g
LEFT JOIN team_user_relation t ON g.team_id=t.team_id
WHERE g.audit_state!='0' AND g.del_flag='0'

	AND g.create_date >= #{createDateStart}


	AND g.create_date <= #{createDateEnd}

GROUP BY t.user_id
) m

SELECT count(id) FROM project_declare a
WHERE a.del_flag='0' AND a.status='9'

	AND a.create_date >= #{createDateStart}


	AND a.create_date <= #{createDateEnd}

SELECT d.label,t2.state,SUM(t2.num) as num from ( select CASE WHEN t1.state='' THEN '1' ELSE t1.state END as state,t1.num from ( select IFNULL(sse.curr_state,1) state ,COUNT(*) num from sys_student_expansion sse inner JOIN ( select * from ( SELECT g.p_name,t.user_id,t.team_id,t.user_type FROM g_contest g LEFT JOIN team_user_relation t ON g.team_id=t.team_id WHERE g.sub_time is not null AND g.del_flag='0' and t.del_flag='0' and (t.state='0' or t.state='4')
AND SUBSTRING(g.sub_time,1,4) = #{year}
union ALL SELECT pd.name,t.user_id,t.team_id,t.user_type FROM project_declare pd LEFT JOIN team_user_relation t ON pd.team_id=t.team_id WHERE pd.apply_time is not null AND pd.del_flag='0' and t.del_flag='0' and (t.state='0' or t.state='4')
AND SUBSTRING(pd.apply_time,1,4) = #{year}
union ALL select pm.p_name,t.user_id,t.team_id,t.user_type from pro_model pm LEFT JOIN team_user_relation t ON pm.team_id=t.team_id LEFT JOIN act_yw a on a.id=pm.actyw_id LEFT JOIN pro_project p on p.id=a.rel_id where p.type is not null and t.del_flag='0' and pm.sub_status='1' and (t.state='0' or t.state='4') and pm.sub_time is not null
AND SUBSTRING(pm.sub_time,1,4) = #{year}
)m GROUP BY m.user_id )t on sse.user_id=t.user_id where t.user_id is not null GROUP BY sse.curr_state )t1 ) t2 LEFT JOIN sys_dict d on d.value=t2.state where d.type='current_sate' and d.del_flag='0' GROUP BY t2.state

select t2.label proType,t2.value proTypeValue,t2.user_id, d1.value curStateValue, d1.label curState , year, count(*) as num from (select d.label,d.value, SUBSTRING(t.sub_time,1,4) year,tm.user_id ,IFNULL(sse.curr_state,'1') curr_state from pro_model t LEFT JOIN act_yw a on a.id=t.actyw_id LEFT JOIN pro_project p on p.id=a.rel_id LEFT JOIN sys_dict d on d.value=p.type LEFT JOIN team_user_relation tm ON tm.team_id=t.team_id LEFT JOIN sys_student_expansion sse on tm.user_id=sse.user_id where d.type='project_style' and p.pro_type='1,' and t.del_flag='0' and t.actyw_id is not null and (tm.state='0' or tm.state='4') and tm.user_type='1' and t.sub_time is not null
AND SUBSTRING(t.sub_time,1,4) = #{year}
UNION select d.label, d.value , SUBSTRING(t.apply_time,1,4) year,tm.user_id ,IFNULL(sse.curr_state,'1') curr_state from project_declare t LEFT JOIN act_yw a on a.id=t.actyw_id LEFT JOIN pro_project p on p.id=a.rel_id LEFT JOIN sys_dict d on d.value=p.type LEFT JOIN team_user_relation tm ON tm.team_id=t.team_id LEFT JOIN sys_student_expansion sse on tm.user_id=sse.user_id where d.type='project_style' and t.del_flag='0' and t.actyw_id is not null and (tm.state='0' or tm.state='4') and tm.user_type='1' and t.apply_time is not null
AND SUBSTRING(t.apply_time,1,4) = #{year}
UNION select d.label, d.value , SUBSTRING(t.sub_time,1,4) year,tm.user_id ,IFNULL(sse.curr_state,'1') curr_state from g_contest t LEFT JOIN act_yw a on a.id=t.actyw_id LEFT JOIN pro_project p on p.id=a.rel_id LEFT JOIN sys_dict d on d.value=p.type LEFT JOIN team_user_relation tm ON tm.team_id=t.team_id LEFT JOIN sys_student_expansion sse on tm.user_id=sse.user_id where d.type='competition_type' and t.del_flag='0' and t.actyw_id is not null and (tm.state='0' or tm.state='4') and tm.user_type='1' and t.sub_time is not null
AND SUBSTRING(t.sub_time,1,4) = #{year}
UNION select d.label,d.value, SUBSTRING(t.sub_time,1,4) year,tm.user_id ,IFNULL(sse.curr_state,'1') curr_state from pro_model t LEFT JOIN act_yw a on a.id=t.actyw_id LEFT JOIN pro_project p on p.id=a.rel_id LEFT JOIN sys_dict d on d.value=p.type LEFT JOIN team_user_relation tm ON tm.team_id=t.team_id LEFT JOIN sys_student_expansion sse on tm.user_id=sse.user_id where d.type='competition_type' and p.pro_type='7,' and t.del_flag='0' and t.actyw_id is not null and (tm.state='0' or tm.state='4') and tm.user_type='1' and t.sub_time is not null
AND SUBSTRING(t.sub_time,1,4) = #{year}
) t2 LEFT JOIN sys_dict d1 on d1.value=t2.curr_state where d1.type='current_sate' and t2.user_id is not null GROUP BY proType , curState

select d.label,d.value proTypeValue,'1' user_type,sse.id,sse.domain from pro_model t LEFT JOIN act_yw a on a.id=t.actyw_id LEFT JOIN pro_project p on p.id=a.rel_id LEFT JOIN sys_dict d on d.value=p.type LEFT JOIN team_user_relation tm ON tm.team_id=t.team_id LEFT JOIN sys_user sse on tm.user_id=sse.id where d.type='project_style' and (tm.state='0' or tm.state='4') and EXISTS ( select 1 from sys_user_role ur left join sys_role sr on ur.role_id=sr.id where ur.user_id=sse.id and sr.biz_type='1' and sr.del_flag='0' ) and t.del_flag='0' and t.actyw_id is not null and tm.user_id is not null and sse.domain is not null and p.pro_type='1,' and t.sub_time is not null
AND SUBSTRING(t.sub_time,1,4) = #{year}
UNION select d.label,d.value proTypeValue,'1' user_type,sse.id,sse.domain from pro_model t LEFT JOIN act_yw a on a.id=t.actyw_id LEFT JOIN pro_project p on p.id=a.rel_id LEFT JOIN sys_dict d on d.value=p.type LEFT JOIN team_user_relation tm ON tm.team_id=t.team_id LEFT JOIN sys_user sse on tm.user_id=sse.id where d.type='competition_type' and (tm.state='0' or tm.state='4') and EXISTS ( select 1 from sys_user_role ur left join sys_role sr on ur.role_id=sr.id where ur.user_id=sse.id and sr.biz_type='1' and sr.del_flag='0' ) and t.del_flag='0' and t.actyw_id is not null and tm.user_id is not null and sse.domain is not null and p.pro_type='7,'and t.sub_time is not null
AND SUBSTRING(t.sub_time,1,4) = #{year}
UNION select d.label, d.value proTypeValue ,'1' user_type,sse.id ,sse.domain from project_declare t LEFT JOIN act_yw a on a.id=t.actyw_id LEFT JOIN pro_project p on p.id=a.rel_id LEFT JOIN sys_dict d on d.value=p.type LEFT JOIN team_user_relation tm ON tm.team_id=t.team_id LEFT JOIN sys_user sse on tm.user_id=sse.id where d.type='project_style' and (tm.state='0' or tm.state='4') and EXISTS ( select 1 from sys_user_role ur left join sys_role sr on ur.role_id=sr.id where ur.user_id=sse.id and sr.biz_type='1' and sr.del_flag='0' ) and t.del_flag='0' and t.actyw_id is not null and tm.user_id is not null and sse.domain is not null and t.apply_time is not null
AND SUBSTRING(t.apply_time,1,4) = #{year}
UNION select d.label, d.value proTypeValue ,'1' user_type,sse.id ,sse.domain from g_contest t LEFT JOIN act_yw a on a.id=t.actyw_id LEFT JOIN pro_project p on p.id=a.rel_id LEFT JOIN sys_dict d on d.value=p.type LEFT JOIN team_user_relation tm ON tm.team_id=t.team_id LEFT JOIN sys_user sse on tm.user_id=sse.id where d.type='competition_type' and (tm.state='0' or tm.state='4') and EXISTS ( select 1 from sys_user_role ur left join sys_role sr on ur.role_id=sr.id where ur.user_id=sse.id and sr.biz_type='1' and sr.del_flag='0' ) and t.del_flag='0' and t.actyw_id is not null and tm.user_id is not null and sse.domain is not null and t.sub_time is not null
AND SUBSTRING(t.sub_time,1,4) = #{year}
