mapper.select:
select t3.label,t.name ,count(*) as num from sys_office t LEFT JOIN( select t1.year, t2.label,t2.value,t1.officeId,t1.pid ,t2.project_name from ( select SUBSTRING(pd.apply_time,1,4) year,pd.leader,so.id officeId,pd.name,pd.type pType,pd.id ,pd.actyw_id ,ay.rel_id pid from project_declare pd LEFT join act_yw ay on ay.id=pd.actyw_id LEFT join sys_user su on su.id=pd.leader LEFT join sys_office so on so.id=su.office_id LEFT JOIN team_user_relation tm ON tm.team_id=pd.team_id where pd.apply_time is not null and pd.del_flag='0' and ay.rel_id is not null and su.office_id is not null and tm.user_type='2' UNION select SUBSTRING(g.sub_time,1,4) year,g.declare_id,so.id officeId,g.p_name,g.type pType,g.id ,g.actyw_id ,ay.rel_id pid from g_contest g LEFT join act_yw ay on ay.id=g.actyw_id LEFT join sys_user su on su.id=g.declare_id LEFT join sys_office so on so.id=su.office_id LEFT JOIN team_user_relation tm ON tm.team_id=g.team_id where g.sub_time is not null and g.del_flag='0' and ay.rel_id is not null and su.office_id is not null and tm.user_type='2' UNION select SUBSTRING(pm.sub_time,1,4) year ,pm.declare_id,so.id officeId,pm.p_name name,pm.pro_category pType,pm.id,pm.actyw_id ,ay.rel_id pid from pro_model pm LEFT join act_yw ay on ay.id=pm.actyw_id LEFT join sys_user su on su.id=pm.declare_id LEFT join sys_office so on so.id=su.office_id LEFT JOIN team_user_relation tm ON tm.team_id=pm.team_id where pm.sub_time is not null and pm.del_flag='0' and ay.rel_id is not null and su.office_id is not null and tm.user_type='2' ) t1 LEFT JOIN ( select d.label ,d.value ,p.project_name,p.pro_type,p.id pid from sys_dict d inner JOIN pro_project p on d.value=p.type where d.type='project_style' and p.type is not null and p.pro_type='1,' and d.del_flag='0' UNION select d.label ,d.value ,p.project_name,p.pro_type,p.id pid from sys_dict d inner JOIN pro_project p on d.value=p.type where d.type='competition_type' and p.type is not null and p.pro_type='7,' and d.del_flag='0' ) t2 on t1.pid=t2.pid where 1=1
AND t1.year = #{year}
)t3 on t.id=t3.officeId where t.grade =2 and t3.officeId is not null GROUP BY label,name
