SELECT

FROM cms_page_lat a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy} ${page.orderByType}


		ORDER BY a.update_date DESC

SELECT

FROM cms_page_lat a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy} ${page.orderByType}


