mapper.select:
SELECT

FROM act_yw_clazz a

WHERE a.id = #{id}

SELECT

FROM act_yw_clazz a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.theme = #{theme}


		AND a.type = #{type}


		AND a.clazz LIKE
			'%'||#{clazz}||'%'
			'%'+#{clazz}+'%'
			concat('%',#{clazz},'%')


		AND a.alias LIKE
			'%'||#{alias}||'%'
			'%'+#{alias}+'%'
			concat('%',#{alias},'%')


		AND a.remarks = #{remarks}




		ORDER BY ${page.orderBy} ${page.orderByType}

SELECT

FROM act_yw_clazz a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy} ${page.orderByType}

mapper.update:
UPDATE act_yw_clazz SET
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
