FROM act_yw_gnode a LEFT JOIN act_yw_gnode p ON p.id = a.parent_id LEFT JOIN act_yw_node c ON c.id = a.node_id LEFT JOIN act_yw_node cp ON cp.id = p.node_id LEFT JOIN act_yw_group b ON b.id = a.group_id LEFT JOIN act_yw yw ON yw.group_id = a.group_id WHERE a.type = #{type}
