mapper.select:
SELECT

FROM act_yw_status a

WHERE a.id = #{id}

SELECT

FROM act_yw_status a


	a.del_flag = #{DEL_FLAG_NORMAL}

		and a.id !=#{id}


		and a.state=#{state}


		and a.alias=#{alias}


		and a.status=#{status}


		and a.gtype=#{gtype}




		ORDER BY ${page.orderBy} ${page.orderByType}


		ORDER BY a.gtype,a.update_date DESC

SELECT

FROM act_yw_status a


	a.del_flag = '0'

		and a.id !=#{id}


		and a.state=#{state}


		and a.gtype=#{gtype}

SELECT

FROM act_yw_status a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy} ${page.orderByType}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE act_yw_status SET
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
