mapper.select:
SELECT

FROM sys_cas_anzhi a

WHERE a.id = #{id}

SELECT
FROM sys_cas_anzhi a
a.del_flag = '0'
AND a.enable = #{enable}
AND a.rutype = #{rutype}
AND a.ruid = #{ruid}
AND a.rname LIKE '%'||#{rname}||'%' '%'+#{rname}+'%' concat('%',#{rname},'%')
AND a.name LIKE '%'||#{rcname}||'%' '%'+#{rcname}+'%' concat('%',#{rcname},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

mapper.update:
UPDATE sys_cas_anzhi SET
	del_flag = '1'
WHERE id = #{id}
