mapper.select:
SELECT

FROM cms_site a

WHERE a.id = #{id}

SELECT
FROM cms_site a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.id = #{id}
AND a.parent_id = #{parent.id}
AND a.parent_ids LIKE '%'||#{parentIds}||'%' '%'+#{parentIds}+'%' concat('%',#{parentIds},'%')
AND a.config_id = #{config.id}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')

mapper.update:
UPDATE cms_site



			when id=#{item.id} then #{item.parent.id}




			when id=#{item.id} then #{item.parentIds}




			when id=#{item.id} then #{item.config.id}




			when id=#{item.id} then #{item.name}




			when id=#{item.id} then #{item.isCurr}




			when id=#{item.id} then #{item.isZzd}




			when id=#{item.id} then #{item.updateBy.id}




			when id=#{item.id} then #{item.updateDate}




			when id=#{item.id} then #{item.remarks}




WHERE id in

	#{item.id}

UPDATE cms_site SET
	parent_id = #{parent.id},
	parent_ids = #{parentIds}
WHERE id = #{id}

UPDATE cms_site SET
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id} OR parent_ids LIKE '%,'||#{id}||',%'

UPDATE cms_site SET
	del_flag = #{DEL_FLAG_DELETE}
	WHERE id in

		#{id}
