mapper.select:
SELECT

FROM cms_tpl a

WHERE a.id = #{id}

SELECT
FROM cms_tpl a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy} ${page.orderByType}
ORDER BY a.update_date DESC

SELECT

FROM cms_tpl a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy} ${page.orderByType}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE cms_tpl



			when id=#{item.id} then #{item.top.id}




			when id=#{item.id} then #{item.type.id}




			when id=#{item.id} then #{item.name}




			when id=#{item.id} then #{item.updateBy.id}




			when id=#{item.id} then #{item.updateDate}




			when id=#{item.id} then #{item.remarks}




WHERE id in

	#{item.id}

UPDATE cms_tpl SET
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE cms_tpl SET
	del_flag = #{DEL_FLAG_DELETE}
	WHERE id in

