mapper.select:
SELECT

FROM oa_notify a

WHERE a.id = #{id}

		SELECT

			,
				r.read_flag,
				r.operate_flag

		FROM oa_notify a



			JOIN oa_notify_record r ON r.oa_notify_id = a.id

		WHERE a.del_flag = #{DEL_FLAG_NORMAL}



			 AND r.user_id = #{userId}


			AND (a.title LIKE  concat('%',#{title},'%')

        )


        AND a.otype = #{otype}


        AND a.TYPE = #{type}


        AND a.STATUS = #{status}


        AND a.STATUS = '1'


        AND a.SEND_TYPE = #{sendType}


        AND a.s_id = #{sId}


        AND r.read_flag = #{readFlag}

    ORDER BY a.update_date DESC

SELECT a.ID AS "id", a.otype AS "otype", a.TYPE AS "type", a.TITLE AS "title", a.CONTENT AS "content", a.FILES AS "files", a.STATUS AS "status", a.CREATE_BY AS "createBy.id", a.CREATE_DATE AS "createDate", a.UPDATE_BY AS "updateBy.id", a.UPDATE_DATE AS "updateDate", a.REMARKS AS "remarks", a.DEL_FLAG AS "delFlag", a.effective_date AS "effectiveDate", a.end_date AS "endDate", a.send_type AS "sendType", a.s_id AS "sid", a.source AS "source", a.views AS "views" , a.rread_flag, a.roperate_flag
FROM (
( SELECT a.ID AS "id", a.otype AS "otype", a.TYPE AS "type", a.TITLE AS "title", a.CONTENT AS "content", a.FILES AS "files", a.STATUS AS "status", a.CREATE_BY AS "CREATE_BY", a.CREATE_DATE AS "CREATE_DATE", a.UPDATE_BY AS "UPDATE_BY", a.UPDATE_DATE AS "UPDATE_DATE", a.REMARKS AS "remarks", a.DEL_FLAG AS "DEL_FLAG", a.effective_date AS "effective_date", a.end_date AS "end_date", a.send_type AS "send_type", a.s_id AS "s_id", a.source AS "source", a.views AS "views", r.ID AS "RId", r.USER_ID AS "RUSER_ID", r.OFFICE_ID AS "ROFFICE_ID", r.READ_FLAG AS "RREAD_FLAG", r.READ_DATE AS "RREAD_DATE", r.operate_flag AS "Roperate_flag", r.out_team AS "Rout_team" FROM oa_notify a LEFT JOIN oa_notify_record r ON r.oa_notify_id = a.id AND r.user_id = #{currentUser.id} AND a.otype = '0' WHERE a.del_flag = 0
AND a.type not IN (3,4,8,9) )
UNION ALL( SELECT a.ID AS "id", a.otype AS "otype", a.TYPE AS "type", a.TITLE AS "title", a.CONTENT AS "content", a.FILES AS "files", a.STATUS AS "status", a.CREATE_BY AS "CREATE_BY", a.CREATE_DATE AS "CREATE_DATE", a.UPDATE_BY AS "UPDATE_BY", a.UPDATE_DATE AS "UPDATE_DATE", a.REMARKS AS "remarks", a.DEL_FLAG AS "DEL_FLAG", a.effective_date AS "effective_date", a.end_date AS "end_date", a.send_type AS "send_type", a.s_id AS "s_id", a.source AS "source", a.views AS "views", r.ID AS "RId", r.USER_ID AS "RUSER_ID", r.OFFICE_ID AS "ROFFICE_ID", r.READ_FLAG AS "RREAD_FLAG", r.READ_DATE AS "RREAD_DATE", r.operate_flag AS "Roperate_flag", r.out_team AS "Rout_team" FROM oa_notify a LEFT JOIN oa_notify_record r ON r.oa_notify_id = a.id AND r.office_id = #{currentUser.professional} LEFT JOIN oa_notify_reoffilter rf ON rf.oa_notify_id = a.id AND rf.user_id = #{currentUser.id} WHERE a.del_flag = 0
AND a.otype = '1' AND a.type not IN (3,4,8,9)
)
) a WHERE a.del_flag = #{DEL_FLAG_NORMAL}
AND(a.ruser_id = #{currentUser.id} OR a.roffice_id = #{currentUser.professional})
AND a.roffice_id = #{currentUser.professional}
AND a.ruser_id = #{currentUser.id}
AND (a.TITLE LIKE concat('%',#{title},'%') or a.content LIKE concat('%',#{title},'%') )
AND a.otype = #{otype}
AND a.TYPE = #{type}
AND a.STATUS = #{status}
AND a.STATUS = '1'
AND a.SEND_TYPE = #{sendType}
AND a.s_id = #{sId}
AND a.rread_flag = #{readFlag}
ORDER BY
a.rread_flag ASC,
a.update_date DESC

SELECT r.user_id userId,
FROM oa_notify a left JOIN oa_notify_record r ON r.oa_notify_id = a.id WHERE a.del_flag = #{DEL_FLAG_NORMAL}
AND a.create_by = #{userId}
AND r.user_id is not null
AND (a.TITLE LIKE concat('%',#{title},'%') or a.content LIKE concat('%',#{title},'%') )
ORDER BY a.update_date DESC

SELECT
	count(1)
FROM oa_notify a

	JOIN oa_notify_record r ON r.oa_notify_id = a.id AND r.user_id = #{currentUser.id}

			AND r.read_flag = #{readFlag}


WHERE a.del_flag = #{DEL_FLAG_NORMAL}

	AND a.STATUS = '1'


	AND a.TITLE = #{title}

mapper.update:
UPDATE oa_notify SET
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:


select count(1) from team_user_relation r join team t on r.team_id = t.id
where r.del_flag = '0' and t.del_flag = '0' and t.state in (0,1) and t.sponsor = #{0} and
r.user_id = #{1}

select temp.title,temp.id,temp.update_date from (SELECT distinct a.title,a.id,a.update_date
FROM oa_notify a left join oa_notify_keyword b on a.id=b.notify_id
WHERE a.del_flag = '0' and a.status='1' and a.type=#{type}

	and a.id!=#{id}


	and

		b.keyword like concat('%',#{key},'%')

) temp
order by temp.update_date desc limit 4

