SELECT a.id, a.href, a.name, a.parent_id AS "parent.id", a.parent_ids FROM sys_menu a INNER JOIN sys_role_menu rm on a.id = rm.menu_id WHERE a.del_flag ='0' AND a.is_show = '1' AND a.parent_ids LIKE #{menu.parentIds} AND rm.role_id in
#{item}
ORDER BY a.sort

SELECT DISTINCT

FROM sys_menu a

where 	a.del_flag = '0' and a.name= #{name}

SELECT COUNT(1) FROM sys_menu a

	 AND  a.name = #{name}
	 AND  a.id != #{id}
	AND a.del_flag = '0'

mapper.update:
UPDATE sys_menu SET
	parent_id = #{parent.id},
	parent_ids = #{parentIds}
WHERE id = #{id}

UPDATE sys_menu SET
	sort = #{sort}
WHERE id = #{id}

UPDATE sys_menu SET
	is_show = #{isShow}
WHERE id = #{id}
