mapper.select:
SELECT

FROM sys_no a

WHERE a.id = #{id}

SELECT

FROM sys_no a

WHERE a.keyss = #{keyss}

SELECT

FROM sys_no a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.keyss = #{keyss}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT

FROM sys_no a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sys_no SET
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
