mapper.select:
SELECT 

FROM sys_num_rule a

WHERE a.id = #{id}

SELECT
FROM sys_num_rule a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM sys_num_rule a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sys_num_rule SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

CREATE EVENT ${event}
on schedule EVERY 1 DAY STARTS date_add(date(sysdate() + 1),interval 0 minute)
ON COMPLETION PRESERVE
do update ${seq} set sequence_id=${num}
