mapper.select:
SELECT 

FROM pro_close_submit a

WHERE a.id = #{id}

SELECT 

FROM pro_close_submit a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM pro_close_submit a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
