	 AND a.app_type = #{appType}
	 AND a.id != #{id}

		SELECT

		FROM sys_number_rule a


			a.del_flag = #{DEL_FLAG_NORMAL}

-- 		GROUP BY a.id


				ORDER BY ${page.orderBy} ${page.orderByType}


				ORDER BY a.update_date DESC, b.sort asc

SELECT

FROM sys_number_rule a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy} ${page.orderByType}


		ORDER BY a.update_date DESC

mapper.update:
